#ifndef L1Trigger_CSCMuonPortCard_h
#define L1Trigger_CSCMuonPortCard_h

#include <vector>
#include <DataFormats/CSCDigi/interface/CSCCorrelatedLCTDigi.h>
#include <DataFormats/MuonData/interface/CSCCorrelatedLCTDigiCollection.h>

class CSCMuonPortCard
{
 public:

  // Rearranged non-persistent container indexed by [endcap][station][sector]
  // Needed for sorting.
  typedef std::vector<std::vector<std::vector<CSCCorrelatedLCTDigi> > > indexed_container;

  CSCMuonPortCard();

  //Method to sort all Correlated LCTs generated by the LCT source.
  //returns a vector of indexed_containers indexed by [BX]
  static std::vector<indexed_container> sort(const CSCCorrelatedLCTDigiCollection& thedigis);
  
 private:
  static std::vector<CSCCorrelatedLCTDigi> getDigis(int endcap, int station, int sector,
						    const CSCCorrelatedLCTDigiCollection& digis);

};

#endif
